mapper.select:

WHERE a.ID = (
	SELECT MIN(d.ID) FROM (
	       SELECT c.id,c.INSPECTION_CODE,c.ACTUAL_START_TIME FROM  c
	       WHERE c.INSPECTION_CODE in (
	             SELECT b.INSPECTION_CODE FROM INSPECTION_TASK_CONTENT b
	             WHERE b.DEVICE_ID = @{_parameter.deviceId, jdbcType=BIGINT}
	             )
	       and c.STATUS = @{_parameter.status, jdbcType=BIGINT}
	  ) d
	WHERE d.ACTUAL_START_TIME = (
	      SELECT MIN(c.ACTUAL_START_TIME) FROM  c
	       WHERE c.INSPECTION_CODE in (
	             SELECT b.INSPECTION_CODE FROM INSPECTION_TASK_CONTENT b
	             WHERE b.DEVICE_ID = @{_parameter.deviceId, jdbcType=BIGINT}
	             )
	       and c.STATUS = @{_parameter.status, jdbcType=BIGINT}
	)
)
