mapper.select:
SELECT a.ID, a.MAINTAIN_CODE, a.PLAN_ID, a.DEVICE_ID, a.ITEM_CONTENT, a.RESULT, a.DESCRIPTON, d.DEVICE_NAME, d.LOCATION_NAME
#order($_parameter.condition,"a.DEVICE_ID DESC")
FROM a LEFT JOIN DEVICE d ON a.DEVICE_ID = d.ID WHERE a.MAINTAIN_CODE = @{_parameter.condition.maintainCode,jdbcType=VARCHAR}
