mapper.select:
select t1.*,t2.JOB_SUBJECT,t2.JOB_NAME,t2.GROUP_NAME,t2.BEAN_OR_CLASS,t2.TARGET_OBJECT,t2.TARGET_METHOD,t2.CRON_EXPRESSION,t2.START_DATE,t2.NEXT_DATE,t2.END_DATE,t2.IGNORE_ERROR,t2.CONCURRENT,t2.DESCRIPTION,t2.STATUS 
from schedule_log t1 LEFT JOIN schedule_job t2 on t1.schedule_id=t2.id
WHERE t1.ID = @{_parameter, jdbcType=NUMERIC}
